            InitializeComponent();
        }

        private void recognizeButton_Click(object sender, RoutedEventArgs e)
        {
            Bitmap bitmap = GetHandWrittenImage();

            Tensor<float> imageData = ConvertImageToTensorData(bitmap);
            var result = _evaluator.Evaluate(imageData);

            // the result is sorted by confidence. so the first is the highest 
            numberLabel.Text = result.First().Digit.ToString();

            // uncomment for debugging
            //var testData = GetTestData().ToTensor().Reshape(new[] { 28, 28 });
            //numberLabel.FontSize = 20;
            //numberLabel.Text = string.Join(Environment.NewLine, result.Select(r => $"{r.Digit} \t {r.Confidence}"));
        }

        public static Tensor<float> ConvertImageToTensorData(Bitmap image)
        {
            var height = image.Size.Height;
            var width = image.Size.Width;
            var data = new DenseTensor<float>(new[] { height, width });
            for (var x = 0; x < height; x++)
            {
                for (var y = 0; y < width; y++)
                {
                    // Be careful about the X/Y direction
                    var color = image.GetPixel(y, x);
                    // 1. Convert to gray
                    // 2. Normalize to [-0.5,0.5]
                    // 3. Turn to black background and white digit like MNIST dataset
                    //data[(x * width) + y] = (float)(0.5 - (color.R + color.G + color.B) / (3.0 * 255));
                    //float value = (float)(0.5 - (color.R + color.G + color.B) / (3.0 * 255));
                    //float value = (float)((color.R + color.G + color.B) / (3.0));
                    float value = (color.R + color.G + color.B) / (3);
                    //data[x, y] = value > 200 ? 255 : 0;
                    data[x, y] = (255 - value);
                    //data[x, y] = value;
                }
            }
            return data;
        }

        private void clearButton_Click(object sender, RoutedEventArgs e)
        {
            inkCanvas.Strokes.Clear();
            numberLabel.Text = "";
        }

        private Bitmap GetHandWrittenImage()
        {
            RenderTargetBitmap renderBitmap = new RenderTargetBitmap((int)inkCanvas.ActualWidth, (int)inkCanvas.ActualHeight, 96d, 96d, PixelFormats.Default);
            renderBitmap.Render(inkCanvas);

            int imageHeight = (int)Math.Sqrt(_evaluator.ExpectedImageInputSize);
            int imageWidth = imageHeight;

            BmpBitmapEncoder encoder = new BmpBitmapEncoder();
            encoder.Frames.Add(BitmapFrame.Create(renderBitmap));

            Bitmap bitmap;
            using (MemoryStream ms = new MemoryStream())
            {
                encoder.Save(ms);
                ms.Flush();

                //get the bitmap bytes from the memory stream
                ms.Position = 0;
                bitmap = new Bitmap(Image.FromStream(ms));
            }

            bitmap = ResizeImage(bitmap, new System.Drawing.Size(imageWidth, imageHeight));
            return bitmap;
        }

        private static Bitmap ResizeImage(Bitmap imgToResize, System.Drawing.Size size)
        {
            Bitmap b = new Bitmap(size.Width, size.Height);
            using (Graphics g = Graphics.FromImage(b))
            {
                g.InterpolationMode = InterpolationMode.HighQualityBicubic;
                g.DrawImage(imgToResize, 0, 0, size.Width, size.Height);
            }
            return b;
        }

